  if (saved) setTheme(saved);
})();
// Ë∑üÈöèÁ≥ªÁªüÈ¢úËâ≤ÂèòÂåñ
(function() {
  // ‰ºòÂÖàÊú¨Âú∞Â≠òÂÇ®
  const saved = localStorage.getItem('theme');
  if (saved) {
    setTheme(saved);
  } else {
